using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace _2017.Day_1
{
    public static class Day1
    {
        public static void Run()
        {
            //Part 1
            Console.WriteLine("Part 1");
            string input = "1122";
            Console.WriteLine($"{input}: {Sum(input)}");
            input = "1111";
            Console.WriteLine($"{input}: {Sum(input)}");
            input = "1234";
            Console.WriteLine($"{input}: {Sum(input)}");
            input = "91212129";
            Console.WriteLine($"{input}: {Sum(input)}");
            
            string actualInput = "";
            Console.WriteLine($"{actualInput}: {Sum(actualInput)}");

            //Part 2
            Console.WriteLine("\n\nPart 2");
            input = "1212";
            Console.WriteLine($"{input}: {SumCircular(input)}");
            input = "1221";
            Console.WriteLine($"{input}: {SumCircular(input)}");
            input = "123425";
            Console.WriteLine($"{input}: {SumCircular(input)}");
            input = "123123";
            Console.WriteLine($"{input}: {SumCircular(input)}");
            input = "12131415";
            Console.WriteLine($"{input}: {SumCircular(input)}");
            
            Console.WriteLine($"{actualInput}: {SumCircular(actualInput)}");
        }

        private static int Sum(string input)
        {
            int sum = 0;
            for (int i = 0; i < input.Length - 1; i++)
            {
                if (input[i] == input[i + 1])
                {
                    if (int.TryParse(input[i].ToString(), out int result))
                    {
                        sum += result;
                    }
                }
            }
            if (input[input.Length - 1] == input[0])
            {
                if (int.TryParse(input[0].ToString(), out int result))
                {
                    sum += result;
                }
            }

            return sum;
        }

        private static int SumCircular(string input)
        {
            int sum = 0;
            int step = input.Length / 2;
            for (int i = 0; i < input.Length; i++)
            {
                int tmpStep = i + step;
                if (tmpStep >= input.Length)
                {
                    tmpStep -= input.Length;
                }
                if (input[i] == input[tmpStep])
                {
                    if (int.TryParse(input[i].ToString(), out int result))
                    {
                        sum += result;
                    }
                }
            }

            return sum;
        }
    }
}
